		<script type="text/javascript">

			var contextPath = "__ATLASSIAN_BASE_URL__";

			(function () {
                var gadget = AJS.Gadget({
                    baseUrl: "__ATLASSIAN_BASE_URL__",
                    useOauth: "/rest/gadget/1.0/currentUser",
					config: {
                        descriptor: function (args) {
							return {
                                fields: [
									{
										userpref: 'problemsURL',
										label: "__MSG_nagios-gadget.config.urllabel__",
										description: "__MSG_nagios-gadget.config.urldesc__",
										type: 'text',
										value: this.getPref('problemsURL')
									},
									AJS.gadget.fields.nowConfigured()
								]
							};
						},
						args: []
					},
					view: {
						enableReload: true,
						template: function(args) {

							var gadget = this;

							function populate(data, stylename, container) {
								 AJS.$(data).each(function() {

		 							var actionsurl = gadget.getBaseUrl() 
										+ "/rest/nagiosglance/latest/actions"
										+ "?host="    + this.host 
										+ "&service=" + this.service;

									container.append(
										AJS.$("<tr/>").append(
											AJS.$("<td/>").append(
												this.last_check
											),
											AJS.$("<td/>").append(
												AJS.$("<a/>").attr({
													target: "_parent",
													href: "__ATLASSIAN_BASE_URL__"
												}).text(this.host)
											),
											AJS.$("<td/>").append(  
												AJS.$("<a/>").attr({
													target: "_parent",
													title: gadgets.util.escapeString(this.status_info),
													href: "__ATLASSIAN_BASE_URL__"
												}).text(this.service)
											),
											AJS.$("<td class='menu'/>").append(
												AJS.$("<a/>").addClass("lnk aui-dd-link")
												.attr("href",  actionsurl)
												.append(
													AJS.$("<span/>").addClass("aui-icon aui-icon-small aui-iconfont-configure")
												)
											)
										).attr(
											{ class : stylename }
										)
									);
								});
							}

							AJS.$(document.body).delegate("a.aui-dd-link", "click", function (e) {

								var $this = AJS.$(this);

								if (!$this.data("dropdown-bound")) {

									$this.data("dropdown-bound", true);

									e.preventDefault();
									e.stopPropagation();

									AJS.Dropdown.create({
										trigger: this,
										autoScroll: false,
										ajaxOptions:
										{
											cache: false,
											formatSuccess: function (response) {

												var html= AJS.$("<div class='aui-list' />");
												var list = AJS.$("<ul />").appendTo(html);

												AJS.$(response.actions).each(function(){
													var listItemNode = AJS.$("<li/>").addClass("aui-list-item");
													var linkNode = AJS.$("<a/>").addClass("aui-list-item-link")
														.attr("href", gadget.getBaseUrl() + this.path).text(this.title);
													listItemNode.append(linkNode);
													list.append(listItemNode);
												});

												return html;

												/*
												var options = [
													{
														title : "__MSG_nagios-gadget.actions.viewserv__",
														key   : "/cgi-bin/nagios3/extinfo.cgi?type=1&host=" + this.host
													},
													{
														title : "__MSG_nagios-gadget.actions.viewhost__",
														key   : "/cgi-bin/nagios3/extinfo.cgi?type=1&host=" + this.host
													},
													{
														title : "__MSG_nagios-gadget.actions.acknowledge__",
														key   : "/cgi-bin/nagios3/extinfo.cgi?type=1&host=" + this.host
													},
													{
														title : "__MSG_nagios-gadget.actions.pinghost__",
														key   : "/cgi-bin/nagios3/extinfo.cgi?type=1&host=" + this.host
													},
													{
														title : "__MSG_nagios-gadget.actions.copyhost__",
														key   : "/cgi-bin/nagios3/extinfo.cgi?type=1&host=" + this.host
													},
													{
														title : "__MSG_nagios-gadget.actions.related__",
														key   : "/cgi-bin/nagios3/extinfo.cgi?type=1&host=" + this.host
													},
													{
														title : "__MSG_nagios-gadget.actions.create__",
														key   : "/cgi-bin/nagios3/extinfo.cgi?type=1&host=" + this.host
													}
												];


												var html= AJS.$("<div class='aui-list' />");
												var list = AJS.$("<ul />").appendTo(html);

												AJS.$(options).each(function() {
													var listItemNode = AJS.$("<li/>").addClass("aui-list-item");
													var linkNode = AJS.$("<a/>").addClass("aui-list-item-link")
														.attr("href", gadget.getBaseUrl() + this.key)
														.text(this.title);
													listItemNode.append(linkNode);
													list.append(listItemNode);
												});

												return html;
												*/
											}
										}
                                	});

									$this.click();
								}
							});

							gadget.showLoading();

                            var serviceList = AJS.$("<table/>");

							populate(args.dataProblems.problems.critical.items, 'critical', serviceList);
							populate(args.dataProblems.problems.warning.items,  'warning',  serviceList);

							summary = AJS.$("<div/>").append(
								AJS.$("<span/>").append(
									"c: " + args.dataProblems.problems.critical.total
								),
								AJS.$("<span/>").append(
									"w: " + args.dataProblems.problems.warning.total
								)
							);

							this.getView().html(serviceList);
                            this.getView().append(summary);

                            gadget.hideLoading();
                        },
                        args: [{
                            key: "dataProblems",
                            ajaxOptions: function() {
                                return {
                                    url: this.getPref('problemsURL')
                                };
                            }
                        }]
                    }
                });
            })();
		</script>
