Module.Content:
		#requireResource("com.atlassian.gadgets.publisher:ajs-gadgets")
		#includeResources()

		<style TYPE="text/css">
			table {
				width: 100%;
				padding:4px;
			}
		</style>

		<script type="text/javascript">
			(function () {
                var gadget = AJS.Gadget({
                    baseUrl: "__ATLASSIAN_BASE_URL__",
                    useOauth: "/rest/gadget/1.0/currentUser",
					config: {
                        descriptor: function (args) {
							return {
                                fields: [
									{
										userpref: 'problemsURL',
										label: 'problems URL:',
										description: 'URL de WSNagios problems',
										type: 'text',
										value: this.getPref('problemsURL')
									},
									AJS.gadget.fields.nowConfigured()
								]
                            };
                        },
                        args: []
                    },
                    view: {
						enableReload: true,
                        template: function(args) {
                            var gadget = this;

                            var projectList = AJS.$("<table/>");

                            AJS.$(args.dataProblems.problems.critical.items).each(function() {
                                projectList.append(
                                    AJS.$("<tr/>").append(
                                    	AJS.$("<td/>").append(
	                                        this.last_check
	                                    ),
	                                    AJS.$("<td/>").append(
	                                       	AJS.$("<a/>").attr({
	                                            target: "_parent",
	                                            href: "__ATLASSIAN_BASE_URL__"
	                                        }).text(this.host)
	                                    ),
	                                    AJS.$("<td/>").append(  
	                                        AJS.$("<a/>").attr({
	                                            target: "_parent",
	                                            title: gadgets.util.escapeString(this.status_info),
	                                            href: "__ATLASSIAN_BASE_URL__"
	                                        }).text(this.service)
	                                    )
                                    )
                                );
                            });

                            AJS.$(args.dataProblems.problems.warning.items).each(function() {
                                projectList.append(
                                    AJS.$("<tr/>").append(
                                    	AJS.$("<td/>").append(
	                                        this.last_check
	                                    ),
	                                    AJS.$("<td/>").append(
	                                       	AJS.$("<a/>").attr({
	                                            target: "_parent",
	                                            href: "__ATLASSIAN_BASE_URL__"
	                                        }).text(this.host)
	                                    ),
	                                    AJS.$("<td/>").append(  
	                                        AJS.$("<a/>").attr({
	                                            target: "_parent",
	                                            title: gadgets.util.escapeString(this.status_info),
	                                            href: "__ATLASSIAN_BASE_URL__"
	                                        }).text(this.service)   
	                                    )
                                    )
                                );
                            });

                            gadget.getView().html(projectList);
                        },
                        args: [{
                            key: "dataProblems",
                            ajaxOptions: function() {
                                return {
                                    url: this.getPref('problemsURL')
                                };
                            }
                        }]
                    }
                });
            })();
		</script>
